/**
 * Normal Cypress wait, but checking for CY_VIDEO_MODE
 * @param {number} time - Time in milliseconds.
 * @param {object} options - Options object to get passed to cy.wait()
 * @returns 
 */
function videoWait(time, options = {log: true}) {
    if (Cypress.env('CY_VIDEO_MODE') != true) {
        return
    }
    Cypress.log()
    cy.wait(time, options)
}

Cypress.Commands.add('videoWait', {prevSubject : false}, videoWait)